
public class Array_Implementation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int Emp_id[] = {10,20,30,40,50};
		String Emp_Name[] = {"Archit","Harsh","Harish","Anupam","Rishabh"};
		
		System.out.println("Emp_id\t\tEmp_Name");
		for(int i=0;i<5;i++) {
			
			System.out.println(Emp_id[i]+"\t\t"+Emp_Name[i]);
		}

	}

}
